import { useEffect, useState } from 'react';
import classes from './ApiDashboard.module.css';
import { useDispatch, useSelector } from 'react-redux';
import { fetchApi } from '../../store/action';

const ApiDashboard = () => {
  const [show, setShow] = useState(false);

  const store = useSelector(state => state);
  const dispatch = useDispatch();

  const data = store.apiData;
  console.log(`ApiDashboard`, store.apiData);

  useEffect(() => {
    dispatch(fetchApi());
  }, [dispatch]);

  return (
    <div className={classes.Box}>
      {data.map(el => (
        <div className={classes.Container} key={el.id}>
          <h3>{el.name}</h3>
          <p>{el.first_brewed}</p>
          <img
            onMouseEnter={() => setShow(true)}
            onMouseLeave
            className={classes.image}
            src={el.image_url}
            alt={''}
          ></img>
          <p>
            {el.volume.value} {el.volume.unit}
          </p>
          <p>Price : {el.ebc} e </p>
          <button className={classes.Btn}>Add to cart</button>
          <div className={classes.ingredients}>
            {el.ingredients.malt.map((el, i) => (
              <div key={i}>{el.name}</div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ApiDashboard;
