import { call, put, takeEvery } from 'redux-saga/effects';
import { REGISTER_USER, registerUserSuccess } from '../action';
import { axios } from 'axios';

const API = users => {
  axios
    .post('https://auth-backend-api.herokuapp.com/register', users)
    .then(res => console.log(res.j));
};

function* registerUsers({ user }) {
  try {
    const response = yield call(API, user);
    console.log(`response`, response);
    yield put({ registerUserSuccess, response });
  } catch (e) {
    console.log(e.message);
  }
}

function* userSaga() {
  yield takeEvery(REGISTER_USER, registerUsers);
}

export default userSaga;
