import { useState } from 'react';
import classes from './SignUp.module.css';
import { Link } from 'react-router-dom';

const SignUp = () => {
  const [data, setData] = useState({
    username: null,
    email: null,
    password: null,
  });

  const [error, setError] = useState({
    username: '',
    email: '',
    password: '',
  });

  console.log(error);

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
      case 'username':
        const username =
          value.length > 0 && value.length < 6
            ? 'Username must be 5 characters long!'
            : '';
        setError({ username: username });
        break;
      case 'email':
        const email = value !== '' && regEmail.test(value)
          ? ''
          : 'Email is not valid!';
        setError({ email: email });
        break;
      case 'password':
        const password =
          value.length < 8
            ? 'Password must be 8 characters long!'
            : '';
        setError({ password: password });
        break;
      default:
        break;
    }
  };

  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Sign Up:</h1>
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="text"
          name="username"
          placeholder={'Username'}
        />
        {error.username !== '' ? (
          <label className={classes.errorBoxUsername}>
            {error.username}
          </label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          placeholder={'E-mail'}
        />
        {error.email !== '' ? (
          <label className={classes.errorBoxEmail}>
            {error.email}
          </label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          placeholder={'Password'}
        />
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="passwordSecond"
          placeholder={'Password'}
        />
        <button className={classes.Btn}>Sing Up</button>
        <p className={classes.p}>
          You have account?Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Login'}>
            Login
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default SignUp;
