import {
  REGISTER_USER,
  REGISTER_USER_SUCCESS,
  LOGIN_USER,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_FAIL,
} from './action';

const initialState = {
  Auth: false,
  registerAuth: false,
  errorEmail: '',
  errorPassword: '',
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
      return { ...state };
    case REGISTER_USER_SUCCESS:
      return {
        ...state,
        registerAuth: action.payload,
      };
    case LOGIN_USER:
      return { ...state };
    case LOGIN_USER_SUCCESS:
      return {
        ...state,
        Auth: action.payload,
      };
    case LOGIN_USER_FAIL:
      const arr = !state.Auth
        ? [(action.payload.email = ''), action.payload.password]
        : null;
      console.log('reducer', arr);
      return {
        ...state,
        errorEmail: arr[0],
        errorPassword: arr[1],
      };
    default:
      return state;
  }
};

export default reducer;
