import { call, put, takeLatest } from 'redux-saga/effects';
import {
  REGISTER_USER,
  registerUserSuccess,
  LOGIN_USER,
  loginUserSuccess,
} from '../action';
import axios from 'axios';

const API_POST = users => {
  axios.post(
    'https://auth-backend-api.herokuapp.com/api/users/register',
    users
  );
};

const API_LOGIN = user => {
  const response = axios
    .post(
      'https://auth-backend-api.herouapp.com/api/users/login',
      user
    )
    .then(res => ({
      res,
    }))
    .catch(err => {
      let message =
        typeof err.response !== 'undefined'
          ? err.response.data.message
          : err.message;
      console.warn('error', message);
    });
  return response;
};

function* loginUsers({ user }) {
  try {
    const response = yield call(API_LOGIN, user);
    if (response) yield put(loginUserSuccess(response));
    if (!response) yield console.log(response);
  } catch (e) {
    console.log(e.message);
  }
}

function* registerUsers({ user }) {
  try {
    const response = yield call(API_POST, user);
    yield put(registerUserSuccess({ response }));
  } catch (e) {
    console.log(e.message);
  }
}

function* userSaga() {
  yield takeLatest(REGISTER_USER, registerUsers);
  yield takeLatest(LOGIN_USER, loginUsers);
}

export default userSaga;
