import {
  REGISTER_USER,
  REGISTER_USER_SUCCESS,
  REGISTER_USER_FAIL,
  LOGIN_USER,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_FAIL,
} from './action';

const initialState = {
  Auth: '',
  registerAuth: false,
  errorEmail: '',
  errorPassword: '',
  errorRegister: '',
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
      return { ...state };
    case REGISTER_USER_SUCCESS:
      return {
        ...state,
        registerAuth: action.payload,
      };
    case REGISTER_USER_FAIL:
      const arrReg = !state.registerAuth ? action.register : null;
      console.log('reducer', arrReg);
      return {
        ...state,
        errorRegister: arrReg !== undefined ? arrReg : '',
      };
    case LOGIN_USER:
      return { ...state };
    case LOGIN_USER_SUCCESS:
      const token = localStorage.getItem('myData');
      return {
        ...state,
        Auth: JSON.parse(token),
      };
    case LOGIN_USER_FAIL:
      const arr = !state.Auth
        ? [action.payload.email, action.payload.password]
        : null;
      return {
        ...state,
        errorEmail: arr[0] !== undefined ? arr[0] : '',
        errorPassword: arr[1] !== undefined ? arr[1] : '',
      };
    default:
      return state;
  }
};

export default reducer;
