import classes from './Cart.module.css';
import { useDispatch, useSelector } from 'react-redux';
import { increment } from '../../store/action';

const Cart = () => {
  const store = useSelector(state => state);
  const dispatch = useDispatch();

  console.log(store.cart);

  const increment = id => {
    dispatch(incrementid);
  };

  return (
    <div className={classes.Box}>
      {store.cart.map((el, i) =>
        el.id != null ? (
          <div key={i} className={classes.Container}>
            <div className={classes.ContainerLeft}>
              <h2 className={classes.h2}>{el.name}</h2>
              <img className={classes.image} src={el.image} />
              <p className={classes.p}>Price: {el.price}</p>
            </div>
            <div className={classes.ContainerRight}>
              <button className={classes.Btn}>-</button>
              <p className={classes.p1}>{el.value}</p>
              <button
                value={el.id}
                onClick={() => increment(el.id)}
                className={classes.Btn}
              >
                +
              </button>
              <p className={classes.p2}>Total Price:</p>
            </div>
          </div>
        ) : null
      )}
    </div>
  );
};

export default Cart;
