import { useState } from 'react';
import classes from './SignUp.module.css';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { registerUser } from '../../store/action';

const SignUp = () => {
  const [data, setData] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
  });

  const [error, setError] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
  });

  console.log(error);

  const store = useSelector(state => state);
  const dispatch = useDispatch();

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  let regNumber =
    /((^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{3,}))$/;

  const validateFild = (value, name) => {
    if (
      name === 'username' &&
      value.length !== 0 &&
      value.length < 6
    ) {
      return 'Username must be 6 characters long!';
    }
    if (
      name === 'email' &&
      value.length !== 0 &&
      !regEmail.test(value)
    )
      return 'Email is not valid!';
    if (
      name === 'password' &&
      value.length !== 0 &&
      !regNumber.test(value)
    )
      return 'Password must have min 3 character, 1 uppercase and 1 lowercase';
    if (
      name === 'password2' &&
      value.length !== 0 &&
      value !== data.password
    )
      return 'Password dont match!';
    return null;
  };

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;
    const errMessage = validateFild(value, name);
    setError({ ...error, [name]: errMessage });
    setData({ ...data, [name]: value });
  };

  const onClick = () => {
    dispatch(registerUser(data));
  };

  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Sign Up:</h1>
        {!store.registerAuth &&
        store.errorRegister !== '' &&
        store.errorRegister !== undefined ? (
          <div className={classes.errorBox}>
            {store.errorRegister}
          </div>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="text"
          name="username"
          placeholder={'Username'}
        />

        <label className={classes.errorBox}>{error.username}</label>

        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          placeholder={'E-mail'}
        />
        {error.email !== '' ? (
          <label className={classes.errorBox}>{error.email}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          placeholder={'Password'}
        />
        {error.password !== '' ? (
          <label className={classes.errorBox}>
            <p className={cla}>{error.password}</p>
          </label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password2"
          placeholder={'Password'}
        />
        {error.password2 !== '' ? (
          <label className={classes.errorBox}>
            {error.password2}
          </label>
        ) : null}
        <button
          disabled={
            error.username !== null ||
            error.email !== null ||
            error.password !== null ||
            error.password2 !== null
              ? true
              : false
          }
          onClick={e => onClick(e)}
          className={classes.Btn}
        >
          Sing up
        </button>
        <p className={classes.p}>
          You have account?Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Login'}>
            Login
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default SignUp;
