import { useState, useEffect } from 'react';
import classes from './SignUp.module.css';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { registerUser } from '../../store/action';

const SignUp = () => {
  const [data, setData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [secondPassword, setSecondPassword] = useState('');

  const [error, setError] = useState({
    username: '',
    email: '',
    password: '',
    passwordSecond: '',
  });

  const dispatch = useDispatch();

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  let regNumber =
    /((^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{3,}))$/;

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
      case 'username':
        const username =
          value !== '' && value.length < 6
            ? 'Username must be 6 characters long!'
            : 'ok';
        setError({ ...error, username: username });
        setData({ ...data, username: value });

        break;
      case 'email':
        const email =
          value !== '' && !regEmail.test(value)
            ? 'Email is not valid!'
            : 'ok';
        setError({ ...error, email: email });
        setData({ ...data, email: value });

        break;
      case 'password':
        const passwords =
          value !== '' && !regNumber.test(value)
            ? 'Password must have min 3 character, 1 uppercase and 1 lowercase'
            : 'ok';
        setError({ ...error, password: passwords });
        setData({ ...data, password: value });

        break;
      case 'passwordSecond':
        const password =
          value !== '' && value !== data.password
            ? 'Password dont match!'
            : 'ok';
        setError({ ...error, passwordSecond: password });

        setSecondPassword(value);
        break;
      default:
        break;
    }
  };

  const onClick = () => {
    dispatch(registerUser(data));
    setData({
      username: '',
      email: '',
      password: '',
    });
    setSecondPassword('');
  };

  useEffect(() => {}, [onClick]);

  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Sign Up:</h1>
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="text"
          name="username"
          value={data.username}
          placeholder={'Username'}
        />
        {error.username !== 'ok' && error.username !== '' ? (
          <label className={classes.errorBox}>{error.username}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          value={data.email}
          placeholder={'E-mail'}
        />
        {error.email !== 'ok' && error.email !== '' ? (
          <label className={classes.errorBox}>{error.email}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          value={data.password}
          placeholder={'Password'}
        />
        {error.password !== 'ok' && error.password !== '' ? (
          <label className={classes.errorBox}>{error.password}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="passwordSecond"
          value={secondPassword}
          placeholder={'Password'}
        />
        {error.passwordSecond !== 'ok' &&
        error.passwordSecond !== '' ? (
          <label className={classes.errorBox}>
            {error.passwordSecond}
          </label>
        ) : null}
        <button
          disabled={
            error.username !== 'ok' ||
            error.email !== 'ok' ||
            error.password !== 'ok' ||
            error.passwordSecond !== 'ok'
              ? true
              : false
          }
          onClick={e => onClick(e)}
          className={classes.Btn}
        >
          Sing up
        </button>
        <p className={classes.p}>
          You have account?Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Login'}>
            Login
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default SignUp;
