import { useState } from 'react';
import classes from './Login.module.css';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { loginUser } from '../../store/action';

const Login = () => {
  const [data, setData] = useState({
    email: '',
    password: '',
  });

  const [error, setError] = useState({
    email: '',
    password: '',
  });

  const dispatch = useDispatch();

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  let regNumber =
    /((^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{3,}))$/;

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
      case 'email':
        const email =
          value !== '' && !regEmail.test(value)
            ? 'Email is not valid!'
            : 'ok';
        setError({ ...error, email: email });
        setData({ ...data, email: value });

        break;
      case 'password':
        const passwords =
          value !== '' && !regNumber.test(value)
            ? 'Password must have min 3 character, 1 uppercase and 1 lowercase'
            : 'ok';
        setError({ ...error, password: passwords });
        setData({ ...data, password: value });

        break;
      default:
        break;
    }
  };

  const onClick = () => {
    dispatch(loginUser(data));
    setData({
      email: '',
      password: '',
    });
  };
  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Login:</h1>
        <div></div>
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          value={data.email}
          placeholder={'E-mail'}
        />
        {error.email !== 'ok' && error.email !== '' ? (
          <label className={classes.errorBox}>{error.email}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          value={data.password}
          placeholder={'Password'}
        />
        {error.password !== 'ok' && error.password !== '' ? (
          <label className={classes.errorBox}>{error.password}</label>
        ) : null}
        <button onClick={e => onClick(e)} className={classes.Btn}>
          Log in
        </button>
        <p>
          You dont have account? Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Register'}>
            Sign up
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default Login;
