import { call, put, takeLatest } from 'redux-saga/effects';
import {
  REGISTER_USER,
  registerUserSuccess,
  registerUserFail,
  LOGIN_USER,
  loginUserSuccess,
  loginUserFail,
  currentUser,
  setRegisterAuth,
} from '../action';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

const API_REGISTER = async users => {
  const response = await axios.post(
    'https://auth-backend-api.herokuapp.com/api/users/register',
    users
  );

  return response.data;
};

const API_LOGIN = async user => {
  const response = await axios.post(
    'https://auth-backend-api.herokuapp.com/api/users/login',
    user
  );
  return response;
};

function* loginUsers({ user }) {
  try {
    const response = yield call(API_LOGIN, user);
    const token = response.data.token;
    const decoded = jwt_decode(token);

    yield put(loginUserSuccess(true));
    yield put(setRegisterAuth(false));
    yield localStorage.setItem('token', token);
    yield put(currentUser(decoded));
  } catch (e) {
    console.log(response.data);
    // yield put(
    //   loginUserFail(response.data.email, response.data.password)
    // );
  }
}

function* registerUsers({ user }) {
  try {
    const response = yield call(API_REGISTER, user);
    console.log(`Register User`, response);
    if (response.status === 200) yield put(registerUserSuccess(true));
    if (response.status === 400)
      yield put(registerUserFail(response.data.email));
  } catch (e) {
    console.log(e.message);
  }
}

function* userSaga() {
  yield takeLatest(REGISTER_USER, registerUsers);
  yield takeLatest(LOGIN_USER, loginUsers);
}

export default userSaga;
