import { useState } from 'react';
import classes from './Login.module.css';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from '../../store/action';

const Login = () => {
  const [data, setData] = useState({
    email: '',
    password: '',
  });

  const store = useSelector(state => state);
  const dispatch = useDispatch();


  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
      case 'email':
        const email =
          value !== '' && !regEmail.test(value)
            ? 'Email is not valid!'
            : 'ok';
        setError({ ...error, email: email });
        setData({ ...data, email: value });

        break;
      case 'password':
        const passwords =
          value !== '' && !regNumber.test(value)
            ? 'Password must have min 3 character, 1 uppercase and 1 lowercase'
            : 'ok';
        setError({ ...error, password: passwords });
        setData({ ...data, password: value });

        break;
      default:
        break;
    }
  };

  const onClick = () => {
    dispatch(loginUser(data));
    setData({
      email: '',
      password: '',
    });
  };
  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Login:</h1>
        <div className={classes.errorBox}>{store.errorEmail}</div>
        <div className={classes.errorBox}>{store.errorPassword}</div>
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          value={data.email}
          placeholder={'E-mail'}
        />

        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          value={data.password}
          placeholder={'Password'}
        />

        <button onClick={e => onClick(e)} className={classes.Btn}>
          Log in
        </button>
        <p>
          You dont have account? Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Register'}>
            Sign up
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default Login;
