import {
  REGISTER_USER,
  REGISTER_USER_SUCCESS,
  REGISTER_USER_FAIL,
  LOGIN_USER,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_FAIL,
  LOGOUT,
  USER,
} from './action';

const initialState = {
  Auth: false,
  registerAuth: false,
  errorEmail: '',
  errorPassword: '',
  errorRegister: '',
  user: {},
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
      return { ...state };
    case REGISTER_USER_SUCCESS:
      return {
        ...state,
        registerAuth: action.payload,
      };
    case REGISTER_USER_FAIL:
      const arrReg = !state.registerAuth ? action.register : null;
      return {
        ...state,
        errorRegister: arrReg !== undefined ? arrReg : '',
      };
    case LOGIN_USER:
      return { ...state };
    case LOGIN_USER_SUCCESS:
      return {
        ...state,
        Auth: action.payload,
      };
    case LOGIN_USER_FAIL:
      let arr = null;
      if (!state.Auth) {
        arr = [action.payload.email, action.payload.password];
      }
      if (state.Auth) {
        arr = null;
      }
      console.log(arr);
      console.log(state.Auth);
      return {
        ...state,
        errorEmail: arr[0] !== undefined ? arr[0] : '',
        errorPassword: arr[1] !== undefined ? arr[1] : '',
      };
    case USER:
      return {
        ...state,
        user: action.payload,
      };
    case LOGOUT:
      return {
        ...state,
        Auth: action.payload,
      };
    default:
      return state;
  }
};

export default reducer;
