import { useState } from 'react';
import classes from './Login.module.css';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { registerUser } from '../../store/action';

const Login = () => {
  const [data, setData] = useState({
    email: '',
    password: '',
  });

  const [error, setError] = useState({
    email: '',
    password: '',
  });

  const dispatch = useDispatch();

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  let regNumber =
    /((^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{3,}))$/;

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
      case 'username':
        const username =
          value !== '' && value.length < 6
            ? 'Username must be 6 characters long!'
            : 'ok';
        setError({ ...error, username: username });
        setData({ ...data, username: value });

        break;
      case 'email':
        const email =
          value !== '' && !regEmail.test(value)
            ? 'Email is not valid!'
            : 'ok';
        setError({ ...error, email: email });
        setData({ ...data, email: value });

        break;
      case 'password':
        const passwords =
          value !== '' && !regNumber.test(value)
            ? 'Password must have min 3 character, 1 uppercase and 1 lowercase'
            : 'ok';
        setError({ ...error, password: passwords });
        setData({ ...data, password: value });

        break;
      case 'passwordSecond':
        const password =
          value !== '' && value !== data.password
            ? 'Password dont match!'
            : 'ok';
        setError({ ...error, passwordSecond: password });

        setSecondPassword(value);
        break;
      default:
        break;
    }
  };

  const onClick = () => {
    dispatch(registerUser(data));
    setData({
      username: '',
      email: '',
      password: '',
    });
    setSecondPassword('');
  };
  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Login:</h1>
        <input
          className={classes.Input}
          type="text"
          placeholder={'Username'}
        />
        <input
          className={classes.Input}
          type="Password"
          placeholder={'Password'}
        />
        <button className={classes.Btn}>Log in</button>
        <p>
          You dont have account? Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Register'}>
            Sign up
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default Login;
