import { useState } from 'react';
import classes from './SignUp.module.css';
import { Link } from 'react-router-dom';

const SignUp = () => {
  const [data, setData] = useState({
    username: null,
    email: null,
    password: null,
  });

  const [disable, setDisable] = useState(false);

  const [error, setError] = useState({
    username: '',
    email: '',
    password: '',
    passwordSecond: '',
  });

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  let regNumber =
    /((^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{3,}))$/;

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;

    switch (name) {
      case 'username':
        const username =
          value.length > 0 && value.length < 6
            ? 'Username must be 6 characters long!'
            : '';
        setError({ ...error, username: username });
        if (value.length >= 6) {
          setData({ ...data, username: value });
        }
        break;
      case 'email':
        const email =
          value !== '' && !regEmail.test(value)
            ? 'Email is not valid!'
            : '';
        setError({ ...error, email: email });
        if (regEmail.test(value)) {
          setData({ ...data, email: value });
        }
        break;
      case 'password':
        const passwords =
          value !== '' && !regNumber.test(value)
            ? 'Password must have min 3 character, 1 uppercase and 1 lowercase'
            : '';
        setError({ ...error, password: passwords });
        if (regNumber.test(value)) {
          setData({ ...data, password: value });
        }
        break;
      case 'passwordSecond':
        const password =
          value !== '' && value !== data.password
            ? 'Password dont match!'
            : '';
        setError({ ...error, passwordSecond: password });
        break;
      default:
        break;
    }
  };

  const enabledBtn = () => {
    if (
      error.username === '' &&
      error.email === '' &&
      error.password === '' &&
      error.passwordSecond === ''
    )
      setDisable(true);
  };

  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Sign Up:</h1>
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="text"
          name="username"
          placeholder={'Username'}
        />
        {error.username !== '' ? (
          <label className={classes.errorBox}>{error.username}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          placeholder={'E-mail'}
        />
        {error.email !== '' ? (
          <label className={classes.errorBox}>{error.email}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          placeholder={'Password'}
        />
        {error.password !== '' ? (
          <label className={classes.errorBox}>{error.password}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="passwordSecond"
          placeholder={'Password'}
        />
        {error.passwordSecond !== '' ? (
          <label className={classes.errorBox}>
            {error.passwordSecond}
          </label>
        ) : null}
        <button
          disabled={disable}
          className={classes.Btn}
          style={{ textDecoration: 'none' }}
          to={disable === true ? '/' : ''}
        >
          Sing up
        </button>{' '}
        <p className={classes.p}>
          You have account?Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Login'}>
            Login
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default SignUp;
