import { call, put, takeLatest } from 'redux-saga/effects';
import {
  REGISTER_USER,
  registerUserSuccess,
  LOGIN_USER,
  loginUserSuccess,
} from '../action';
import axios from 'axios';

const API_POST = users => {
  axios.post(
    'https://users-e53c6-default-rtdb.europe-west1.firebasedatabase.app/register.json',
    users
  );
};

const API_LOGIN = user => {
  axios.post(
    'https://users-e53c6-default-rtdb.europe-west1.firebasedatabase.app/register',
    user
  );
  return { token: response.data.accessToken };
};

function* loginUsers({ user }) {
  try {
    const response = yield call(API_LOGIN, user);
    yield put(loginUserSuccess({ user }));
  } catch (e) {
    console.log(e.message);
  }
}

function* registerUsers({ user }) {
  try {
    const response = yield call(API_POST, user);
    yield put(registerUserSuccess({ user }));
  } catch (e) {
    console.log(e.message);
  }
}

function* userSaga() {
  yield takeLatest(REGISTER_USER, registerUsers);
  yield takeLatest(LOGIN_USER, loginUsers);
}

export default userSaga;
