import { call, put, takeLatest } from 'redux-saga/effects';
import {
  REGISTER_USER,
  registerUserSuccess,
  LOGIN_USER,
  loginUserSuccess,
} from '../action';
import axios from 'axios';

const API_POST = users => {
  axios.post(
    'https://auth-backend-api.herokuapp.com/api/',
    users
  );
};

const API_LOGIN = user => {
  console.log(`SAGA`, user);
  const response = axios.post(
    'https://users-e53c6-default-rtdb.europe-west1.firebasedatabase.app/login.json',
    user
  );
  return console.log(response.accessToken);
};

function* loginUsers({ user }) {
  try {
    const response = yield call(API_LOGIN, user);
    yield put(loginUserSuccess(response));
  } catch (e) {
    console.log(e.message);
  }
}

function* registerUsers({ user }) {
  try {
    const response = yield call(API_POST, user);
    yield put(registerUserSuccess({ user }));
  } catch (e) {
    console.log(e.message);
  }
}

function* userSaga() {
  yield takeLatest(REGISTER_USER, registerUsers);
  yield takeLatest(LOGIN_USER, loginUsers);
}

export default userSaga;
