import {
  REGISTER_USER,
  REGISTER_USER_SUCCESS,
  REGISTER_USER_FAIL,
  LOGIN_USER,
  LOGIN_USER_FAIL,
  SET_REGISTER_AUTH,
} from './action';

const initialState = {
  registerAuth: false,
  error: {},
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
      return { ...state };
    case REGISTER_USER_SUCCESS:
      return {
        ...state,
        registerAuth: action.payload,
      };
    case SET_REGISTER_AUTH:
      return {
        ...state,
        registerAuth: action.payload,
      };
    case REGISTER_USER_FAIL:
      console.log(state.error);
      return {
        ...state,
        error: action.payload,
      };
    case LOGIN_USER:
      return { ...state };
    case LOGIN_USER_FAIL:
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};

export default reducer;
