import {
  REGISTER_USER,
  REGISTER_USER_SUCCESS,
  REGISTER_USER_FAIL,
  LOGIN_USER,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_FAIL,
  LOGOUT,
  USER,
  SET_REGISTER_AUTH,
} from './action';

const initialState = {
  Auth: false,
  registerAuth: false,
  errorEmail: '',
  errorPassword: '',
  errorRegister: '',
  user: {},
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
      return { ...state };
    case REGISTER_USER_SUCCESS:
      return {
        ...state,
        registerAuth: action.payload,
      };
    case SET_REGISTER_AUTH:
      return {
        ...state,
        Auth: action.payload,
      };
    case REGISTER_USER_FAIL:
      return {
        ...state,
        errorRegister: action.register,
      };
    case LOGIN_USER:
      return { ...state };
    case LOGIN_USER_SUCCESS:
      return {
        ...state,
        Auth: action.payload,
      };
    case LOGIN_USER_FAIL:
      return {
        ...state,
        errorEmail: action.payload.email,
        errorPassword: action.payload.password,
      };
    case USER:
      return {
        ...state,
        user: action.payload,
      };
    case LOGOUT:
      return {
        ...state,
        Auth: action.payload,
      };
    default:
      return state;
  }
};

export default reducer;
