import { useState } from 'react';
import classes from './SignUp.module.css';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { registerUser } from '../../store/action';

const SignUp = () => {
  const [data, setData] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
  });

  const [error, setError] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
  });

  console.log(error);

  const store = useSelector(state => state);
  const dispatch = useDispatch();

  let regEmail =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  let regNumber =
    /((^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{3,}))$/;

  const validateFild = value => {
    if (value.length < 6)
      return 'Username must be 6 characters long!';
    if (!regEmail.test(value)) return 'Email is not valid!';
    if (!regNumber.test(value))
      return 'Password must have min 3 character, 1 uppercase and 1 lowercase';
    if (value !== data.password) return;
  };

  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;
    const errMessage = validateFild(value);
    setError({ ...error, [name]: errMessage });

    // switch (name) {
    //   case 'username':
    //     const username =
    //       value !== '' && value.length < 6
    //         ? 'Username must be 6 characters long!'
    //         : 'ok';
    //     setError({ ...error, username: username });
    //     setData({ ...data, username: value });

    //     break;
    //   case 'email':
    //     const email =
    //       value !== '' && !regEmail.test(value)
    //         ? 'Email is not valid!'
    //         : 'ok';
    //     setError({ ...error, email: email });
    //     setData({ ...data, email: value });

    //     break;
    //   case 'password':
    //     const passwords =
    //       value !== '' && !regNumber.test(value)
    //         ? 'Password must have min 3 character, 1 uppercase and 1 lowercase'
    //         : 'ok';
    //     setError({ ...error, password: passwords });
    //     setData({ ...data, password: value });

    //     break;
    //   case 'password2':
    //     const password =
    //       value !== '' && value !== data.password
    //         ? 'Password dont match!'
    //         : 'ok';
    //     setError({ ...error, password2: password });

    //     setData({ ...data, password2: value });
    //     break;
    //   default:
    //     break;
    // }
  };

  const onClick = () => {
    dispatch(registerUser(data));
  };

  return (
    <div className={classes.Container}>
      <div className={classes.Box}>
        <h1>Sign Up:</h1>
        {!store.registerAuth &&
        store.errorRegister !== '' &&
        store.errorRegister !== undefined ? (
          <div className={classes.errorBox}>
            {store.errorRegister}
          </div>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="text"
          name="username"
          placeholder={'Username'}
        />

        <label className={classes.errorBox}>{error.username}</label>

        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="email"
          name="email"
          placeholder={'E-mail'}
        />
        {error.email !== 'ok' && error.email !== '' ? (
          <label className={classes.errorBox}>{error.email}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password"
          placeholder={'Password'}
        />
        {error.password !== 'ok' && error.password !== '' ? (
          <label className={classes.errorBox}>{error.password}</label>
        ) : null}
        <input
          onChange={e => handleChange(e)}
          className={classes.Input}
          type="Password"
          name="password2"
          placeholder={'Password'}
        />
        {error.password2 !== 'ok' && error.password2 !== '' ? (
          <label className={classes.errorBox}>
            {error.password2}
          </label>
        ) : null}
        <button
          disabled={
            error.username !== 'ok' ||
            error.email !== 'ok' ||
            error.password !== 'ok' ||
            error.password2 !== 'ok'
              ? true
              : false
          }
          onClick={e => onClick(e)}
          className={classes.Btn}
        >
          Sing up
        </button>
        <p className={classes.p}>
          You have account?Then go to{' '}
          <Link style={{ textDecoration: 'none' }} to={'/Login'}>
            Login
          </Link>{' '}
          page!
        </p>
      </div>
    </div>
  );
};

export default SignUp;
