import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import classes from './eachApiFetch.module.css';
import { fetchSingleApi } from '../../store/action';
import { useParams } from 'react-router-dom';

const EachApiFetch = () => {
  let { id } = useParams();
  console.log(id);

  const store = useSelector(state => state);
  const dispatch = useDispatch();

  const data = store.singleApiData;
  console.log(`Single Api call`, store.singleApiData);

  useEffect(() => {
    dispatch(fetchSingleApi(id));
  }, []);

  return (
    <div className={classes.Box}>
      <div className={classes.Container}>
        <img className={classes.Image} src={data.image_url} />
        <div className={classes.ContainerLeft}>
          <h1>{data.name}</h1>
          <p>Attenuation level: {data.attenuation_level}</p>
          <p>Brewers tips: {data.brewers_tips}</p>
          <p>Contributed by: {data.contributed_by}</p>
          <p>Description: {data.description}</p>
          <p>Price: {data.ebc}</p>
          <p>First brewed: {data.first_brewed}</p>
          <p>tagline</p>
        </div>
      </div>
    </div>
  );
};

export default EachApiFetch;
