import { call, put, takeLatest } from 'redux-saga/effects';
import { REGISTER_USER, registerUserSuccess } from '../action';
import axios from 'axios';

const instance = axios.create(
  'https://auth-backend-api.herokuapp.com/'
);

const API = users => {
  instance.post('/register.json', users);
};

function* registerUsers({ user }) {
  try {
    const response = yield call(API, user);
    console.log(`Response`, response);
    yield put(registerUserSuccess(response));
  } catch (e) {
    console.log(e.message);
  }
}

function* userSaga() {
  yield takeLatest(REGISTER_USER, registerUsers);
}

export default userSaga;
